import * as ns from './index';

/** 
 * The following line triggers compiler error due to the usage of relative path in its name
 * only when the first import statement is absent.
 * This is weird, because typescript spec prohibits such a use.
 * {@link https://github.com/Microsoft/TypeScript/blob/master/doc/spec.md#12.2}
 * Similar pattern minus the relative path is documented in 
 * {@link https://www.typescriptlang.org/docs/handbook/declaration-files/templates/module-plugin-d-ts.html}
 */ 
declare module './index'{
	export const exported_from_augment:number;
}

